insert into Genre (id, name, description) values ('6846c776bf4211ec9d640242ac120002', 'Ficción', 'Se denomina ficción a la simulación de la realidad que realizan las obras como literarias, cinematográficas, historietísticas, de animación u de otro tipo, cuando presentan un mundo imaginario al receptor.');
insert into Genre (id, name, description) values ('7df699fcbf4211ec9d640242ac120002', 'Terror', 'Es un género literario que se define por la sensación que causa: miedo. La característica más importante del género horror es el efecto del que se causa en la audiencia, el horror necesariamente debe provocar miedo en el espectador.');
insert into Genre (id, name, description) values ('864f7254bf4211ec9d640242ac120002', 'Comedia', 'Es el género dramático opuesto a la tragedia y, por lo tanto, relacionado casi siempre con historias con final feliz. Esa lectura epicúrea, placentera y optimista.');
insert into Genre (id, name, description) values ('8ff437b8bf4211ec9d640242ac120002', 'Suspenso', 'Es un género que intenta generar miedo en el receptor y conmoverlo a partir de sucesos inesperados.');
